sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    //Get current date/time
    var rightNow = new GlideDateTime();
    //Get When needed date/time
    var whenNeeded = new GlideDateTime(current.u_when_needed);

    //Today plus 1
    //var today = rightNow.addDaysLocalTime(1);
    // 	gs.addInfoMessage("Today = " + today);
    // 	current.setAbortAction(true);
    //
    if (whenNeeded.before(rightNow)) {
        gs.addErrorMessage("When needed should be after " + rightNow);
		gs.addErrorMessage("When needed should be after " + whenNeeded);
        current.setAbortAction(true);
    }

})(current, previous);
