sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }
    //Retain What needed value in case record had this field previously set by user.
    //Records that already have this field set will correctly set it below
    var what_needed = g_form.getValue('u_what_needed');

    //Clear default options from choices list so that more specific choices can be set based on
    //Request type that user selects
    g_form.clearOptions('u_what_needed');

    //Set Choice options based off of users Reqeust type selection
    if (newValue === 'hr') {
        g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
        g_form.addOption('u_what_needed', 'hr2', 'Human Resources 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
    }

    if (newValue === 'facilities') {
        g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
        g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
    }

    if (newValue === 'legal') {
        g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
        g_form.addOption('u_what_needed', 'legal2', 'Legal 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
    }

	//Resets What needed selection if it was set prior to opening this record
	if (isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', what_needed);
	}

	//Set tab to 2 tab on load
	g_tabs2Sections.setActive(1);
	g_tabs2Sections.markTabMandatoryByField('description');
}
